import React, { Component } from 'react'
import './App.css';
import  NavBar  from './navBar/NavBar';
import Aux from './HOC/AUX'
import MainContainer from './mainContainer/MainContainer';
import Axios from 'axios'



export default class App extends Component {

  state = {
    mode : "main"
  }  


  render() {

    let mainContainerRender;
    if(this.state.mode === "main"){
      mainContainerRender = 
      <div>this is the main page</div>
    }else if(this.state.mode === "shops"){
      mainContainerRender =
      <div>this is shop page</div>
    }else if(this.state.mode === "cart"){
      mainContainerRender =
      <div>this is cart</div>
    }
    return (
      <Aux>

      < NavBar loadMain={this.loadMain} loadShops={this.loadShops} loadCart={this.loadCart}/>
      <MainContainer>
        {mainContainerRender}
      </MainContainer>
    </Aux>
    )
  }
  loadMain = ()=>{
    this.setState({mode : "main"});

  }
  loadShops = ()=>{
    this.setState({mode : "shops"});
  }
  loadCart = ()=>{
    this.setState({mode : "cart"});
  }
}
