import React, { Component } from 'react'
import './App.css';
import  NavBar  from './navBar/NavBar';
import Aux from './HOC/AUX'
import MainContainer from './mainContainer/MainContainer';
import Axios from 'axios'



export default class App extends Component {

  state = {
    mode : "main"
  }  


  render() {

    let mainContainerRender;
    if(this.state.mode === "main"){
      mainContainerRender = 
      <div>this is the main page</div>
    }else if(this.state.mode === "shops"){
      mainContainerRender =
      <div>this is shop page</div>
    }else if(this.state.mode === "cart"){
      mainContainerRender =
      <div>this is cart</div>
    }
    return (
      <Aux>

      < NavBar cM={this.cM}/>
      <MainContainer >
        {mainContainerRender}
      </MainContainer>

    </Aux>
    )
  }
  cM =  (changedMod) =>{
   this.setState({mode:changedMod});
  // console.log(val)
  }
}
