import React, { Component } from 'react'
//import React from 'react'
import Axios from 'axios'


class Shop extends Component {

    state = {data : null }

    fetchData = () =>{
        return new Promise(function(resolve){
            setTimeout(() => {
                const dummyData= {
                    _id:'1',
                    shopName:"someName",
                    items : [{name:'item1',price:21},{name:'item2',price:42}]
                    
                }
                return dummyData    
            }, 5000);
        })

        
        
    }
    componentDidMount(){
        this.fetchData().then(fetchedData =>{
            this.setState({data:fetchedData})
        });
       
    }
    render() {

        let shopRender;
        let data = {...this.state.data}
        
        if (this.state.data === null){
            shopRender = <div>
                <h3>loading animation</h3>
            </div>
            //render loader
        }else{
            //render data
            shopRender = 
                <h3>this.state.shopName</h3>
                data.items.map((item)=>{
                    return <div>
                        <h4>item:{item.name}</h4>
                        <h4>price:{item.price}</h4>
                    </div>
                })
        }
        return (
            <div>
                {shopRender}
            </div>
        )
    }
     
}

export default Shop;
